package day1.Conditions;

public class CountDigitNumberfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int num=12344,count=0;
    
   //  int k=num!=0;
     if(num!=0) {
    	 num=num/10;
     for(int i=0;i==num;i++) {
    	 count++;
     }
     }
     System.out.println("Count  =  "+count);
	}
	
}
