package day1.Conditions;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count=0;
       for(int i=4;i<=20;i++) {
    	   if(i%4==0) {
    	   System.out.println(" "+i);
    	   count++;
    	  
       }
       }
       System.out.println("count= "+count);
	}

}
